$(function() {
$('.comb_wilayad').change(function() {
var id=$(this).val();var dataStringd = 'id='+ id; 
$.ajax({
type: 'POST',
url: ROOT,
data: dataStringd,
timeout: 3000,
success: function(html) {
$(".comb_communed").html(html);	},  
error: function() {alert('La requête n\'a pas abouti'); }
});    
});  
});

